import{createStore}from"vuex";const store=createStore({state:()=>({gameSelectScreen:!0,chosenGame:"",playerChose:"",gameResultsVisible:!1,currentOptions:[],gamesWon:{basic:0,extra:0},gamesPlayed:{basic:0,extra:0}}),getters:{isGameSelectVisible:state=>state.gameSelectScreen,chosenGame:state=>state.chosenGame,playerChose:state=>state.playerChose,isGameResultsVisible:state=>state.gameResultsVisible,currentOptions:state=>state.currentOptions,gamesWon:state=>state.gamesWon,gamesPlayed:state=>state.gamesPlayed},actions:{},mutations:{updateGameSelectScreen(state,payload){state.gameSelectScreen=payload},updateChosenGame(state,payload){state.chosenGame=payload},updatePlayerChose(state,payload){state.playerChose=payload},updateGameResultsVisible(state,payload){state.gameResultsVisible=payload},updateCurrentOptions(state,payload){state.currentOptions=payload},updateGamesWon(state){const game=state.chosenGame;state.gamesWon[game]++},updateGamesPlayed(state){const game=state.chosenGame;state.gamesPlayed[game]++}}});export default store;